document.getElementById("summarizeButton").addEventListener("click",(async()=>{const t=document.getElementById("inputText").value,e=document.getElementById("summary");if(t){e.textContent="Summarizing...";try{const n=await fetch("/summarize",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({text:t})});if(n.ok){const t=await n.json();e.textContent=t.summary}else{const t=await n.json();e.textContent="Error: "+t.error}}catch(t){e.textContent="Error: "+t.message}}else alert("Please enter some text to summarize.")}));